master.installScript:
# Installation Microsoft Teams (nouveau Teams)
$teamsUrl = 'https://go.microsoft.com/fwlink/?linkid=2187327&Lmsrc=groupChatMarketingPageWeb&Cmpid=directDownloadWin64&clcid=0x40c'
$teamsInstaller = "$env:TEMP\Teams_windows_x64.exe"

Write-Host "  Téléchargement Microsoft Teams..." -ForegroundColor Gray
try {
    Invoke-WebRequest -Uri $teamsUrl -OutFile $teamsInstaller -UseBasicParsing -ErrorAction Stop

    Write-Host "  Installation Teams (mode silencieux)..." -ForegroundColor Gray
    $installProc = Start-Process -FilePath $teamsInstaller -ArgumentList '-s' -Wait -NoNewWindow -PassThru

    if ($installProc.ExitCode -eq 0) {
        Write-Host "    [OK] Microsoft Teams installé" -ForegroundColor Green
    } else {
        Write-Host "    [ERREUR] Échec installation Teams (Exit Code: $($installProc.ExitCode))" -ForegroundColor Red
    }

    # Nettoyage
    Remove-Item -Path $teamsInstaller -Force -ErrorAction SilentlyContinue
} catch {
    Write-Host "    [ERREUR] Impossible de télécharger Teams: $_" -ForegroundColor Red
}

# Installation VPN Stormshield avec import AddressBook (2 VPN)
Write-Host "Installation de VPN Stormshield..." -ForegroundColor Cyan

$tempPath = Join-Path $env:TEMP 'vpnstormshield.msi'
try {
    # Télécharger et installer le MSI
    Invoke-WebRequest -Uri 'http://85.90.48.117/vpnssl/vpnstormshield.msi' -OutFile $tempPath -UseBasicParsing
    $proc = Start-Process 'msiexec.exe' -ArgumentList "/i `"$tempPath`" /qn /norestart REBOOT=ReallySuppress" -Wait -PassThru

    if ($proc.ExitCode -ne 0) {
        throw "L'installation MSI a échoué avec le code $($proc.ExitCode)"
    }

    Write-Host "  [OK] VPN Stormshield installé" -ForegroundColor Green

    # Créer le fichier AddressBook.book avec les 2 VPN (Lyon + Paris)
    Write-Host "Configuration des connexions VPN (Lyon + Paris)..." -ForegroundColor Cyan

    $bookContent = @'
{"Entries":[{"Id":0,"Name":"VPN TENOR LYON","Description":null,"AutoMount":false,"Login":"","Password":"Sw==","Address":"vpn.tenorsolutions.com","Port":443,"Otp":false,"FallbackAddress":null,"FallbackPort":443,"IsFavorite":true,"IsLastConnection":true,"CertHash":"46B78E9E4138B72D70359131A27485090C372637B2FF5CE603CAEAB774B744E8"},{"Id":1,"Name":"VPN TENOR PARIS","Description":"VPN de secours ","AutoMount":false,"Login":null,"Password":null,"Address":"vpn-backup.tenorsolutions.com","Port":443,"Otp":false,"FallbackAddress":null,"FallbackPort":443,"IsFavorite":false,"IsLastConnection":false,"CertHash":null}],"EncryptedPassword":true,"EncryptedPasswordSalt":15343168359289697346}
'@

    $bookPath = Join-Path $env:TEMP 'AddressBook.book'
    [System.IO.File]::WriteAllText($bookPath, $bookContent, [System.Text.Encoding]::UTF8)

    # Localiser sslvpn-cli.exe
    $cliPath = "$env:ProgramFiles\Stormshield\SSL VPN Client\Modules\ssl-vpn\Services\sslvpn-cli.exe"
    if (!(Test-Path $cliPath)) {
        $cliPath = "${env:ProgramFiles(x86)}\Stormshield\SSL VPN Client\Modules\ssl-vpn\Services\sslvpn-cli.exe"
    }

    if (Test-Path $cliPath) {
        # Importer l'AddressBook via CLI officielle (SNS v5+)
        Write-Host "  Import des connexions VPN via sslvpn-cli..." -ForegroundColor Cyan
        $importProc = Start-Process -FilePath $cliPath -ArgumentList "import-addressbook --file `"$bookPath`"" -Wait -NoNewWindow -PassThru

        if ($importProc.ExitCode -eq 0) {
            Write-Host "  [OK] Connexions VPN importées (Lyon + Paris)" -ForegroundColor Green
        } else {
            Write-Host "  [ATTENTION] Import CLI échoué (code $($importProc.ExitCode))" -ForegroundColor Yellow
        }
    } else {
        Write-Host "  [ATTENTION] sslvpn-cli.exe non trouvé" -ForegroundColor Yellow
    }

    Write-Host "VPN Stormshield installé avec succès" -ForegroundColor Green

} catch {
    throw "Erreur installation VPN Stormshield: $_"
} finally {
    if (Test-Path $tempPath) { Remove-Item $tempPath -Force -ErrorAction SilentlyContinue }
    if (Test-Path $bookPath) { Remove-Item $bookPath -Force -ErrorAction SilentlyContinue }
}

cegidmanufacturingpmi.installScript:
# Vérification de l'accès au partage réseau
if (!(Test-Path '\\SRVLY3\ManufacturingPmi')) {
    throw "Impossible d'accéder au partage réseau \\SRVLY3\ManufacturingPmi"
}

# Copie locale du répertoire d'installation
$localDir = Join-Path $env:TEMP 'CegidSetup'
if (Test-Path $localDir) {
    Remove-Item $localDir -Recurse -Force
}

Write-Host "Copie des fichiers d'installation depuis le partage réseau..." -ForegroundColor Cyan
Copy-Item '\\SRVLY3\ManufacturingPmi' $localDir -Recurse -Force

# Recherche du fichier MSI
$msiFile = Get-ChildItem -Path $localDir -Filter '*.msi' -Recurse -ErrorAction SilentlyContinue | Select-Object -First 1

if (!$msiFile) {
    throw "Aucun fichier MSI trouvé dans le répertoire d'installation"
}

Write-Host "Installation de Cegid Manufacturing PMI..." -ForegroundColor Cyan
$proc = Start-Process 'msiexec.exe' -ArgumentList "/i `"$($msiFile.FullName)`" /qn /norestart REBOOT=ReallySuppress" -Wait -PassThru

if ($proc.ExitCode -ne 0) {
    Remove-Item $localDir -Recurse -Force -ErrorAction SilentlyContinue
    throw "L'installation a échoué avec le code $($proc.ExitCode)"
}

# Copie des fichiers de configuration
Write-Host "Configuration de Cegid Manufacturing PMI..." -ForegroundColor Cyan
$configPath = 'C:\Program Files (x86)\Cegid\ManufacturingPMI\Config'
if (!(Test-Path $configPath)) {
    New-Item -Path $configPath -ItemType Directory -Force | Out-Null
}

Copy-Item '\\SRVLY3\ManufacturingPmi\Deploiement V16\Config\*' $configPath -Force -Recurse -ErrorAction SilentlyContinue
Copy-Item '\\SRVLY3\ManufacturingPmi\Deploiement V16\PG1LIFE.wdl' 'C:\Program Files (x86)\Cegid\ManufacturingPMI\PG1LIFE.wdl' -Force -ErrorAction SilentlyContinue

# Nettoyer le répertoire temporaire
Remove-Item $localDir -Recurse -Force -ErrorAction SilentlyContinue

Write-Host "Cegid Manufacturing PMI installé avec succès" -ForegroundColor Green
